import * as React from 'react';
import PropTypes from 'prop-types';
import './toggle.scss';

const componentClassName = 'toggle'

const Toggle = ({
  id,
  style,
  name,
  isChecked,
  onChange,
  isSmall,
  isDisabled,
  optionLabels,
}) => {
  return (
    <div
      id={id}
      style={style}
    >
      <div className={"toggle-switch" + (small ? " small-switch" : "")}>
        <input
          type="checkbox"
          className="toggle-switch-checkbox"
          name={name}
          id={id}
          checked={isChecked}
          onChange={e => onChange(e.target.checked)}
          disabled={isDisabled}
        />
        <label className="toggle-switch-label" htmlFor={id}>
          <span
            className={
              isDisabled
                ? "toggle-switch-inner toggle-switch-disabled"
                : "toggle-switch-inner"
            }
            data-on={optionLabels[0]}
            data-off={optionLabels[1]}
          />
        </label>
      </div>    

    </div>
    )
}

Toggle.propTypes = {
  /**
   * The HTML id for this element
   */
  id:PropTypes.string,

  /**
   * The React-written, css properties for this element.
   */
  style:PropTypes.objectOf(PropTypes.string),

  /**
   * The HTML name for this element
   */
  name:PropTypes.string,

  /**
   * Holds the current state of the toggle
   */
  isChecked:PropTypes.bool,

  /**
   * Function called when the input's onChange is triggered
   */
  onChange:PropTypes.func,

  /**
   * Whether toggle should be in small mode
   */
  isSmall:PropTypes.bool,

  /**
   * Whether the toggle is disabled
   */
  isDisabled:PropTypes.bool,

  /**
   * Option labels when toggle is not disabled
   */
  optionLabels:PropTypes.array,
};

Toggle.defaultProps = {
  isChecked:false,
  isSmall:false,
  isDisabled:false,
  optionLabels:["Light", "Dark"],
};

export default Toggle
